import React, { Component } from 'react';
import CardList from './CardList.js'; //is exported as default 
import SearchBox from './SearchBox.js';
import { robots } from './robots.js'; //is not exported as default



//state: an "object" that describes your application that can change and affect it, lives in parent, passed to children component.
//要用class類別元件才能使用state
//class類別元件是"object"因此要用this
class App extends Component {
	constructor() {
		super();
		this.state = {
			robots: robots,
			searchfield: ''
		}
	}

	onSearchChange = event => {
		this.setState() //update state
		const filterdRobots = this.state.robots.filter( robot => {
			return robot.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
		})
		console.log(filterdRobots);
	}

	render() {
		return (
			<div className='tc'>
				<h1> RoboFriends </h1>
				<SearchBox searchChange={this.onSearchChange} />
				<CardList robots={this.state.robots}/>
			</div>
		)
	}

}





export default App;
