import React, { Component } from 'react';
import CardList from './CardList.js'; //is exported as default
import SearchBox from './SearchBox.js';
import { robots } from './robots.js'; //is not exported as default




//state: sth that can change and affect the app, lives in parent, passed to children component.
class App extends Component {
	constructor() {
		super();
		this.state = {
			robots: robots,
			searchfield: ''
		}
	}
	render() {
		return (
			<div className='tc'>
				<h1> RoboFriends </h1>
				<SearchBox />
				<CardList robots={this.state.robots}/>
			</div>
		)
	}

}





export default App;
