import React from 'react';
import Card from './Card.js';

const CardList = ( {robots} ) => {
	return(
		<div>
		{
			robots.map((user, i) => {
				return(
					<Card 
					key={i} //有用loop時要給key
					id={robots[i].id} 
					name={robots[i].name} 
					email={robots[i].email} />
				);
			})
		}
		</div>
	);
}




export default CardList;
